 function snapshotToArray(snapshot) {
   var returnArr = [];
 
   snapshot.forEach(function(childSnapshot) {
       var item = childSnapshot.val();
       item.key = childSnapshot.key;
 
       returnArr.push(item);
   });
 
   return returnArr;
 };
